import React from 'react';
import { fetchUserData, cancelFetch } from '../dataFetcher';
import { Userlist } from './Userlist';

export class Profile extends React.Component {
  constructor(props){
    super(props);
    this.state = { userData: null};
  }

  loadUserData(){
    this.fetchID = fetchUserData(this.props.username, (userData) => {
      this.setState({ userData });
    });
  }

  componentDidMount(){
    this.loadUserData();
  }

  componentWillUnmount(){
   cancelFetch(this.fetchID)
  }

  componentDidUpdate(prevProps){
    if(this.props.username !== prevProps.username){
      cancelFetch(this.fetchID);
    } else {
      this.loadUserData();
    }
  }

  render() {
    const isLoading = this.state.userData === null;

    let className = 'Profile';
      if (isLoading) {
        className += ' loading';
      }

    let name;
      if(isLoading){
        name = 'Loading...';
      } else {
        name = this.state.userData.name;
      }

    let bio;
      if(isLoading){
        bio = 'Loading...';
      } else {
        bio = this.state.userData.bio;
      }

    let friends;
      if(isLoading){
        friends = [];
      } else {
        friends = this.state.userData.friends;
      }

    return (
      <div className={className}>
        <div className="profile-picture">
          {!isLoading && (<img src={this.state.userData.profilePictureUrl} alt="" />)}
        </div>
        <div className="profile-body">
          <h2 style={{fontSize: '2em'}}>{name}</h2>
          <h3 style={{color: '#3b5998'}}>@{this.props.username}</h3>
          <p style={{fontSize: '1.5em'}}>{bio}</p>
          <h3 style={{fontSize: '2em'}}>My friends</h3>
          <Userlist usernames={friends} onChoose={this.props.onChoose} />
        </div>
      </div>
    );
  }
}
